import Tool from "./Tool";

export default class Brush extends Tool {
    constructor(canvas) {
        super(canvas );
        this.listen();
    }

    listen() {
        this.canvas.onmousedown = this.mouseDownHandler.bind(this);
        this.canvas.onmouseup = this.mouseUpHandler.bind(this);
        this.canvas.onmousemove = this.mouseMoveHandler.bind(this);
    }



    mouseMoveHandler(e) {
        if(this.mouseDown) {

            this.ctx.lineTo((e.clientX - e.target.offsetLeft)*0.1, (e.clientY- e.target.offsetTop)*0.1);
           this. ctx.stroke();

            this.ctx.beginPath();
            this.ctx.fill();

            this.ctx.beginPath();
            this.ctx.moveTo((e.clientX - e.target.offsetLeft)*0.1, (e.clientY- e.target.offsetTop)*0.1);
        }
    }

    mouseUpHandler(e) {
        this.mouseDown = false;
        this.ctx.beginPath();
    }

    mouseDownHandler(e){
        this.mouseDown = true;

    }

    draw(x, y) {
        this.ctx.lineTo(x, y);
        this.ctx.stroke();
    }
}
