import {makeAutoObservable} from "mobx";

class CanvasState {
    canvas = null;
    undoList = [];
    redoList = [];

    constructor() {
        makeAutoObservable(this);
    }

    setCanvas(canvas) {
        this.canvas = canvas;
    }

    pushToUndoList(data) {
        this.undoList.push(data);
    }

    pushToRedoList(data) {
        this.redoList.push(data);
    }

    undo() {
        let ctx = this.canvas.getContex('2d')
    }
}

export default new CanvasState();
