import React from 'react';
import brush from './../assets/images/big-paint-brush.svg';
import square from './../assets/images/rounded-black-square-shape.svg';
import circle from './../assets/images/dry-clean.svg'
import eraser from './../assets/images/eraser.svg';
import  './../styles/toolbar.scss';
import toolState from "../store/toolState";
import Brush from "../tools/Brush";
import canvasState from "../store/canvasState";

const Toolbar = () => {
    return (
        <div className="toolbar">
            <div className="toolbar__inner">
                <img src={brush} alt="icon" className="toolbar__icon"  onClick={() => toolState.setTool(new Brush(canvasState.canvas))}/>
                <img src={square} alt="icon" className="toolbar__icon" onClick={() => toolState.setTool(new Brush(canvasState.canvas))}/>
                <img src={circle} alt="icon" className="toolbar__icon"/>
                <img src={eraser} alt="icon" className="toolbar__icon"/>
                <div className="toolbar__input-range">
                    <input type="range" min="0" max="11" step="1"/>
                </div>
                <input type="color"/>
            </div>
        </div>
    );
};

export default Toolbar;
