import toolState from "../store/toolState";

export default class Tool {
    color = "black";
    line = 1;
    constructor(canvas) {
        this.canvas = canvas;
        this.ctx = canvas.getContext('2d');
        this.destroyEvents();
        toolState.setFillColor(this.color);
        toolState.setLineWidth(this.line);
    }

    set fillColor(color) {
        this.ctx.fillStyle = color;
        this.color = color;

    }


    set lineWidth(width) {
        console.log(width);
        this.ctx.lineWidth = width;
        this.line = width;
    }

    destroyEvents() {
        this.canvas.onmousedown = null;
        this.canvas.onmouseup = null;
        this.canvas.onmousemove = null;
    }
}
